import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Lock, User, Eye, EyeOff, CheckCircle, XCircle, Users, Shield, UserPlus } from 'lucide-react';
import Register from '../Register';
import './Login.css';

const Login: React.FC = () => {
  const { login } = useAuth();
  const [identifier, setIdentifier] = useState(''); // Changed from username to identifier
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    try {
      const result = await login(username, password);

      if (result.success) {
        setSuccess(result.message || 'Login successful!');
        // The App component will handle routing based on user type
      } else {
        setError(result.message || 'Login failed');
      }
    } catch (err) {
      setError('An unexpected error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const fillCredentials = (userType: 'user' | 'admin') => {
    if (userType === 'user') {
      setUsername('parishioner');
      setPassword('parish123');
    } else {
      setUsername('admin');
      setPassword('admin123');
    }
  };

  return (
    <div className="auth-login">
      <div className="login-container">
        <div className="login-header">
          <div className="church-logo">
            <img 
              src="/api/placeholder/100/100" 
              alt="St. Patrick's Catholic Church Logo" 
              className="logo-image"
            />
          </div>
          <h1>St. Patrick's Catholic Church</h1>
          <p>Makokoba, Bulawayo - Authentication Portal</p>
        </div>

        {/* Quick Login Options */}
        <div className="quick-login-section">
          <h3>Quick Login Options</h3>
          <div className="quick-login-buttons">
            <button 
              type="button" 
              className="quick-login-btn user-btn"
              onClick={() => fillCredentials('user')}
            >
              <Users size={20} />
              <div>
                <strong>Parishioner</strong>
                <small>Access church content</small>
              </div>
            </button>
            <button 
              type="button" 
              className="quick-login-btn admin-btn"
              onClick={() => fillCredentials('admin')}
            >
              <Shield size={20} />
              <div>
                <strong>Administrator</strong>
                <small>Manage church content</small>
              </div>
            </button>
          </div>
        </div>

        <div className="divider">
          <span>Or enter credentials manually</span>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <div className="input-wrapper">
              <User className="input-icon" size={20} />
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter your username"
                required
                disabled={isLoading}
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-wrapper">
              <Lock className="input-icon" size={20} />
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
                disabled={isLoading}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                disabled={isLoading}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
          </div>

          {error && (
            <div className="message error-message">
              <XCircle size={20} />
              <span>{error}</span>
            </div>
          )}

          {success && (
            <div className="message success-message">
              <CheckCircle size={20} />
              <span>{success}</span>
            </div>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={isLoading || !username || !password}
          >
            {isLoading ? (
              <>
                <div className="spinner"></div>
                Authenticating...
              </>
            ) : (
              'Sign In'
            )}
          </button>
        </form>

        <div className="login-footer">
          <div className="default-credentials">
            <h4>Default Login Credentials:</h4>
            <div className="credentials-info">
              <div className="credential-item">
                <strong>Parishioner:</strong> parishioner / parish123
              </div>
              <div className="credential-item">
                <strong>Administrator:</strong> admin / admin123
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
