import React, { createContext, useContext, useState, ReactNode } from 'react';

export type UserType = 'user' | 'admin' | null;

interface User {
  username: string;
  userType: UserType;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  login: (username: string, password: string) => Promise<{ success: boolean; userType?: UserType; message?: string }>;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Default users
const defaultUsers = [
  { username: 'parishioner', password: 'parish123', userType: 'user' as UserType },
  { username: 'admin', password: 'admin123', userType: 'admin' as UserType }
];

export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(() => {
    // Check if user is already logged in (from localStorage)
    const savedUser = localStorage.getItem('currentUser');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const login = async (username: string, password: string): Promise<{ success: boolean; userType?: UserType; message?: string }> => {
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    const foundUser = defaultUsers.find(
      u => u.username === username && u.password === password
    );

    if (foundUser) {
      const newUser = {
        username: foundUser.username,
        userType: foundUser.userType
      };

      setUser(newUser);
      localStorage.setItem('currentUser', JSON.stringify(newUser));

      return {
        success: true,
        userType: foundUser.userType,
        message: `Welcome ${foundUser.userType === 'admin' ? 'Administrator' : 'Parishioner'}!`
      };
    }

    return {
      success: false,
      message: 'Invalid username or password. Please try again.'
    };
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('currentUser');
  };

  const value = {
    user,
    isAuthenticated: !!user,
    login,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
