import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useLanguage } from '../contexts/LanguageContext';
import { useAdmin } from '../contexts/AdminContext';
import { Menu, X, Globe } from 'lucide-react';
import './Header.css';

const Header: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { language, setLanguage, t } = useLanguage();
  const { getActiveAnnouncement } = useAdmin();
  const location = useLocation();

  const activeAnnouncement = getActiveAnnouncement();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleLanguage = () => {
    setLanguage(language === 'en' ? 'nd' : 'en');
  };

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className="header">
      {/* Urgent Announcements Banner */}
      <div className="urgent-banner">
        <div className="container">
          <p className="urgent-text">
            üì¢ {t('home.urgent')}: Special Christmas Service - December 25th at 8:30 AM
          </p>
        </div>
      </div>

      <div className="header-main">
        <div className="container">
          <div className="header-content">
            {/* Logo and Church Name */}
            <div className="logo-section">
              <div className="logo">
                <span className="cross">‚úù</span>
              </div>
              <div className="church-info">
                <h1 className="church-name">St. Patrick's</h1>
                <p className="church-location">Makokoba, Bulawayo</p>
              </div>
            </div>

            {/* Language Toggle */}
            <div className="language-toggle">
              <button 
                onClick={toggleLanguage}
                className="lang-btn"
                aria-label="Toggle Language"
              >
                <Globe size={20} />
                <span>{language === 'en' ? t('lang.switch_to_ndebele') : t('lang.switch_to_english')}</span>
              </button>
            </div>

            {/* Mobile Menu Button */}
            <button 
              className="mobile-menu-btn"
              onClick={toggleMenu}
              aria-label="Toggle Menu"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>

          {/* Navigation */}
          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li>
                <Link 
                  to="/" 
                  className={`nav-link ${isActive('/') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.home')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/about" 
                  className={`nav-link ${isActive('/about') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.about')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/ministries" 
                  className={`nav-link ${isActive('/ministries') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.ministries')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/outreach" 
                  className={`nav-link ${isActive('/outreach') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.outreach')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/sacraments" 
                  className={`nav-link ${isActive('/sacraments') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.sacraments')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/events" 
                  className={`nav-link ${isActive('/events') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.events')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/contact" 
                  className={`nav-link ${isActive('/contact') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.contact')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/giving" 
                  className={`nav-link nav-link-giving ${isActive('/giving') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.giving')}
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
