import React, { createContext, useContext, useState, ReactNode, useEffect } from 'react';

// Types for admin data
export interface Announcement {
  id: string;
  title: string;
  message: string;
  type: 'urgent' | 'info' | 'event';
  isActive: boolean;
  createdAt: string;
  expiresAt?: string;
}

export interface Event {
  id: string;
  title: string;
  description: string;
  date: string;
  time: string;
  location: string;
  category: 'mass' | 'meeting' | 'social' | 'education' | 'outreach';
  isPublished: boolean;
  createdAt: string;
}

export interface GalleryImage {
  id: string;
  title: string;
  description: string;
  url: string;
  category: 'choir' | 'youth' | 'outreach' | 'mass' | 'events';
  uploadedAt: string;
  isPublished: boolean;
}

export interface ContactInfo {
  address: string;
  phone: string[];
  email: string[];
  officeHours: {
    weekdays: string;
    saturday: string;
    sunday: string;
  };
  staff: {
    name: string;
    position: string;
    phone?: string;
    email?: string;
  }[];
}

export interface MassSchedule {
  weekdays: string;
  saturday: string;
  sunday: string[];
  confession: string[];
  specialServices?: {
    name: string;
    date: string;
    time: string;
  }[];
}

export interface ThemeOfYear {
  id: string;
  year: number;
  title: string;
  subtitle: string;
  verse: string;
  description: string;
  imageUrl: string;
  isActive: boolean;
  createdAt: string;
}

export interface SaintOfDay {
  name: string;
  title: string;
  dates: string;
  description: string;
  quote: string;
  prayer: string;
  imageUrl: string;
  feastDay: string;
}

export interface LiturgicalInfo {
  date: string;
  season: string;
  color: string;
  readings: {
    firstReading: string;
    psalm: string;
    secondReading?: string;
    gospel: string;
  };
}

export interface Ministry {
  id: string;
  name: string;
  description: string;
  imageUrl: string;
  contactPerson: string;
  meetingTime: string;
  isActive: boolean;
  createdAt: string;
}

export interface PrayerIntention {
  id: string;
  name: string;
  email?: string;
  intention: string;
  isUrgent: boolean;
  isPublic: boolean;
  submittedAt: string;
  status: 'pending' | 'approved' | 'prayed';
}

export interface Sacrament {
  id: string;
  name: string;
  description: string;
  imageUrl: string;
  requirements: string[];
  contactInfo: string;
  isActive: boolean;
  createdAt: string;
}

export interface SectionImage {
  id: string;
  section: 'mass_times' | 'confession_times' | 'catechism_times' | 'parish_gallery';
  title: string;
  imageUrl: string;
  isActive: boolean;
  createdAt: string;
}

interface AdminContextType {
  // Authentication
  isAuthenticated: boolean;
  login: (username: string, password: string) => boolean;
  logout: () => void;

  // Announcements
  announcements: Announcement[];
  addAnnouncement: (announcement: Omit<Announcement, 'id' | 'createdAt'>) => void;
  updateAnnouncement: (id: string, announcement: Partial<Announcement>) => void;
  deleteAnnouncement: (id: string) => void;
  getActiveAnnouncement: () => Announcement | null;

  // Events
  events: Event[];
  addEvent: (event: Omit<Event, 'id' | 'createdAt'>) => void;
  updateEvent: (id: string, event: Partial<Event>) => void;
  deleteEvent: (id: string) => void;
  getPublishedEvents: () => Event[];

  // Gallery
  galleryImages: GalleryImage[];
  addImage: (image: Omit<GalleryImage, 'id' | 'uploadedAt'>) => void;
  updateImage: (id: string, image: Partial<GalleryImage>) => void;
  deleteImage: (id: string) => void;
  getPublishedImages: () => GalleryImage[];

  // Contact & Schedule
  contactInfo: ContactInfo;
  updateContactInfo: (info: Partial<ContactInfo>) => void;
  massSchedule: MassSchedule;
  updateMassSchedule: (schedule: Partial<MassSchedule>) => void;

  // Theme of Year
  themesOfYear: ThemeOfYear[];
  addThemeOfYear: (theme: Omit<ThemeOfYear, 'id' | 'createdAt'>) => void;
  updateThemeOfYear: (id: string, theme: Partial<ThemeOfYear>) => void;
  deleteThemeOfYear: (id: string) => void;
  getActiveTheme: () => ThemeOfYear | null;

  // Saint of Day & Liturgical Info
  saintOfDay: SaintOfDay | null;
  liturgicalInfo: LiturgicalInfo | null;
  fetchSaintOfDay: () => Promise<void>;

  // Ministries
  ministries: Ministry[];
  addMinistry: (ministry: Omit<Ministry, 'id' | 'createdAt'>) => void;
  updateMinistry: (id: string, ministry: Partial<Ministry>) => void;
  deleteMinistry: (id: string) => void;
  getActiveMinistries: () => Ministry[];

  // Prayer Intentions
  prayerIntentions: PrayerIntention[];
  addPrayerIntention: (intention: Omit<PrayerIntention, 'id' | 'submittedAt'>) => void;
  updatePrayerIntention: (id: string, intention: Partial<PrayerIntention>) => void;
  deletePrayerIntention: (id: string) => void;
  getPendingIntentions: () => PrayerIntention[];

  // Sacraments
  sacraments: Sacrament[];
  addSacrament: (sacrament: Omit<Sacrament, 'id' | 'createdAt'>) => void;
  updateSacrament: (id: string, sacrament: Partial<Sacrament>) => void;
  deleteSacrament: (id: string) => void;
