export const AdminProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  // Authentication state
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Data states
  const [announcements, setAnnouncements] = useState<Announcement[]>(defaultAnnouncements);
  const [events, setEvents] = useState<Event[]>(defaultEvents);
  const [galleryImages, setGalleryImages] = useState<GalleryImage[]>([]);
  const [contactInfo, setContactInfo] = useState<ContactInfo>(defaultContactInfo);
  const [massSchedule, setMassSchedule] = useState<MassSchedule>(defaultMassSchedule);
  const [themesOfYear, setThemesOfYear] = useState<ThemeOfYear[]>(defaultThemesOfYear);
  const [saintOfDay, setSaintOfDay] = useState<SaintOfDay | null>(null);
  const [liturgicalInfo, setLiturgicalInfo] = useState<LiturgicalInfo | null>(null);
  const [ministries, setMinistries] = useState<Ministry[]>(defaultMinistries);
  const [prayerIntentions, setPrayerIntentions] = useState<PrayerIntention[]>([]);
  const [sacraments, setSacraments] = useState<Sacrament[]>(defaultSacraments);
  const [sectionImages, setSectionImages] = useState<SectionImage[]>(defaultSectionImages);

  // Load data from localStorage on mount
  useEffect(() => {
    const savedAuth = localStorage.getItem('stpatricks_admin_auth');
    const savedAnnouncements = localStorage.getItem('stpatricks_announcements');
    const savedEvents = localStorage.getItem('stpatricks_events');
    const savedImages = localStorage.getItem('stpatricks_gallery');
    const savedContact = localStorage.getItem('stpatricks_contact');
    const savedSchedule = localStorage.getItem('stpatricks_schedule');
    const savedThemes = localStorage.getItem('stpatricks_themes');
    const savedMinistries = localStorage.getItem('stpatricks_ministries');
    const savedPrayerIntentions = localStorage.getItem('stpatricks_prayer_intentions');
    const savedSacraments = localStorage.getItem('stpatricks_sacraments');
    const savedSectionImages = localStorage.getItem('stpatricks_section_images');

    if (savedAuth === 'true') setIsAuthenticated(true);
    if (savedAnnouncements) setAnnouncements(JSON.parse(savedAnnouncements));
    if (savedEvents) setEvents(JSON.parse(savedEvents));
    if (savedImages) setGalleryImages(JSON.parse(savedImages));
    if (savedContact) setContactInfo(JSON.parse(savedContact));
    if (savedSchedule) setMassSchedule(JSON.parse(savedSchedule));
    if (savedThemes) setThemesOfYear(JSON.parse(savedThemes));
    if (savedMinistries) setMinistries(JSON.parse(savedMinistries));
    if (savedPrayerIntentions) setPrayerIntentions(JSON.parse(savedPrayerIntentions));
    if (savedSacraments) setSacraments(JSON.parse(savedSacraments));
    if (savedSectionImages) setSectionImages(JSON.parse(savedSectionImages));

    // Fetch saint of the day on load
    fetchSaintOfDay();
  }, []);

  // Authentication functions
  const login = (username: string, password: string): boolean => {
    // Simple authentication - in production, use proper authentication
    if (username === 'admin' && password === 'stpatricks2024') {
      setIsAuthenticated(true);
      localStorage.setItem('stpatricks_admin_auth', 'true');
      return true;
    }
    return false;
  };

  const logout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('stpatricks_admin_auth');
  };

  // Announcement functions
  const addAnnouncement = (announcement: Omit<Announcement, 'id' | 'createdAt'>) => {
    const newAnnouncement: Announcement = {
      ...announcement,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...announcements, newAnnouncement];
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const updateAnnouncement = (id: string, announcement: Partial<Announcement>) => {
    const updated = announcements.map(a => a.id === id ? { ...a, ...announcement } : a);
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const deleteAnnouncement = (id: string) => {
    const updated = announcements.filter(a => a.id !== id);
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const getActiveAnnouncement = (): Announcement | null => {
    const active = announcements.find(a => a.isActive);
    if (!active) return null;

    // Check if announcement has expired
    if (active.expiresAt && new Date(active.expiresAt) < new Date()) {
      updateAnnouncement(active.id, { isActive: false });
      return null;
    }

    return active;
  };

  // Event functions
  const addEvent = (event: Omit<Event, 'id' | 'createdAt'>) => {
    const newEvent: Event = {
      ...event,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...events, newEvent];
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const updateEvent = (id: string, event: Partial<Event>) => {
    const updated = events.map(e => e.id === id ? { ...e, ...event } : e);
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const deleteEvent = (id: string) => {
    const updated = events.filter(e => e.id !== id);
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const getPublishedEvents = (): Event[] => {
    return events.filter(e => e.isPublished).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  };

  // Gallery functions
  const addImage = (image: Omit<GalleryImage, 'id' | 'uploadedAt'>) => {
    const newImage: GalleryImage = {
      ...image,
      id: Date.now().toString(),
      uploadedAt: new Date().toISOString()
    };
    const updated = [...galleryImages, newImage];
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const updateImage = (id: string, image: Partial<GalleryImage>) => {
    const updated = galleryImages.map(i => i.id === id ? { ...i, ...image } : i);
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const deleteImage = (id: string) => {
    const updated = galleryImages.filter(i => i.id !== id);
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const getPublishedImages = (): GalleryImage[] => {
    return galleryImages.filter(i => i.isPublished);
  };

  // Contact and schedule functions
  const updateContactInfo = (info: Partial<ContactInfo>) => {
    const updated = { ...contactInfo, ...info };
    setContactInfo(updated);
    localStorage.setItem('stpatricks_contact', JSON.stringify(updated));
  };

  const updateMassSchedule = (schedule: Partial<MassSchedule>) => {
    const updated = { ...massSchedule, ...schedule };
    setMassSchedule(updated);
    localStorage.setItem('stpatricks_schedule', JSON.stringify(updated));
  };

  // Theme of Year functions
  const addThemeOfYear = (theme: Omit<ThemeOfYear, 'id' | 'createdAt'>) => {
    const newTheme: ThemeOfYear = {
      ...theme,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...themesOfYear, newTheme];
    setThemesOfYear(updated);
    localStorage.setItem('stpatricks_themes', JSON.stringify(updated));
  };

  const updateThemeOfYear = (id: string, theme: Partial<ThemeOfYear>) => {
    const updated = themesOfYear.map(t => t.id === id ? { ...t, ...theme } : t);
    setThemesOfYear(updated);
    localStorage.setItem('stpatricks_themes', JSON.stringify(updated));
  };

  const deleteThemeOfYear = (id: string) => {
    const updated = themesOfYear.filter(t => t.id !== id);
    setThemesOfYear(updated);
    localStorage.setItem('stpatricks_themes', JSON.stringify(updated));
  };

  const getActiveTheme = (): ThemeOfYear | null => {
    return themesOfYear.find(t => t.isActive) || null;
  };

  // Saint of Day & Liturgical functions
  const fetchSaintOfDay = async () => {
    try {
      // Mock API call - in production, replace with actual Catholic API
      const mockSaint: SaintOfDay = {
        name: "St. Thérèse of Lisieux",
        title: "The Little Flower",
        dates: "1873 - 1897",
        description: "Known for her 'Little Way' of spiritual childhood, St. Thérèse taught that sanctity is achievable by all through simple acts of love and trust in God.",
        quote: "I will spend my heaven doing good on earth.",
        prayer: "St. Thérèse, help us to trust in God's love and find holiness in the ordinary moments of life.",
        imageUrl: "/api/placeholder/200/250",
        feastDay: new Date().toLocaleDateString()
      };

      const mockLiturgical: LiturgicalInfo = {
        date: new Date().toISOString(),
        season: "Ordinary Time",
        color: "green",
        readings: {
          firstReading: "Isaiah 55:10-11",
          psalm: "Psalm 65:10-14",
          gospel: "Matthew 13:1-23"
        }
      };

      setSaintOfDay(mockSaint);
      setLiturgicalInfo(mockLiturgical);
    } catch (error) {
      console.error('Error fetching saint of the day:', error);
    }
  };

  // Ministry functions
  const addMinistry = (ministry: Omit<Ministry, 'id' | 'createdAt'>) => {
    const newMinistry: Ministry = {
      ...ministry,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...ministries, newMinistry];
    setMinistries(updated);
    localStorage.setItem('stpatricks_ministries', JSON.stringify(updated));
  };

  const updateMinistry = (id: string, ministry: Partial<Ministry>) => {
    const updated = ministries.map(m => m.id === id ? { ...m, ...ministry } : m);
    setMinistries(updated);
    localStorage.setItem('stpatricks_ministries', JSON.stringify(updated));
  };

  const deleteMinistry = (id: string) => {
    const updated = ministries.filter(m => m.id !== id);
    setMinistries(updated);
    localStorage.setItem('stpatricks_ministries', JSON.stringify(updated));
  };

  const getActiveMinistries = (): Ministry[] => {
    return ministries.filter(m => m.isActive);
  };

  // Prayer Intention functions
  const addPrayerIntention = (intention: Omit<PrayerIntention, 'id' | 'submittedAt'>) => {
    const newIntention: PrayerIntention = {
      ...intention,
      id: Date.now().toString(),
      submittedAt: new Date().toISOString()
    };
    const updated = [...prayerIntentions, newIntention];
    setPrayerIntentions(updated);
    localStorage.setItem('stpatricks_prayer_intentions', JSON.stringify(updated));
  };

  const updatePrayerIntention = (id: string, intention: Partial<PrayerIntention>) => {
    const updated = prayerIntentions.map(p => p.id === id ? { ...p, ...intention } : p);
    setPrayerIntentions(updated);
    localStorage.setItem('stpatricks_prayer_intentions', JSON.stringify(updated));
  };

  const deletePrayerIntention = (id: string) => {
    const updated = prayerIntentions.filter(p => p.id !== id);
    setPrayerIntentions(updated);
    localStorage.setItem('stpatricks_prayer_intentions', JSON.stringify(updated));
  };

  const getPendingIntentions = (): PrayerIntention[] => {
    return prayerIntentions.filter(p => p.status === 'pending');
  };

  // Sacrament functions
  const addSacrament = (sacrament: Omit<Sacrament, 'id' | 'createdAt'>) => {
    const newSacrament: Sacrament = {
      ...sacrament,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...sacraments, newSacrament];
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const updateSacrament = (id: string, sacrament: Partial<Sacrament>) => {
    const updated = sacraments.map(s => s.id === id ? { ...s, ...sacrament } : s);
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const deleteSacrament = (id: string) => {
    const updated = sacraments.filter(s => s.id !== id);
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const getActiveSacraments = (): Sacrament[] => {
    return sacraments.filter(s => s.isActive);
  };

  // Section Image functions
  const addSectionImage = (image: Omit<SectionImage, 'id' | 'createdAt'>) => {
    const newImage: SectionImage = {
      ...image,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...sectionImages, newImage];
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const updateSectionImage = (id: string, image: Partial<SectionImage>) => {
    const updated = sectionImages.map(i => i.id === id ? { ...i, ...image } : i);
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const deleteSectionImage = (id: string) => {
    const updated = sectionImages.filter(i => i.id !== id);
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const getSectionImages = (section: SectionImage['section']): SectionImage[] => {
    return sectionImages.filter(i => i.section === section && i.isActive);
  };

  return (
    <AdminContext.Provider value={{
      isAuthenticated,
      login,
      logout,
      announcements,
      addAnnouncement,
      updateAnnouncement,
      deleteAnnouncement,
      getActiveAnnouncement,
      events,
      addEvent,
      updateEvent,
      deleteEvent,
      getPublishedEvents,
      galleryImages,
      addImage,
      updateImage,
      deleteImage,
      getPublishedImages,
      contactInfo,
      updateContactInfo,
      massSchedule,
      updateMassSchedule,
      themesOfYear,
      addThemeOfYear,
      updateThemeOfYear,
      deleteThemeOfYear,
      getActiveTheme,
      saintOfDay,
      liturgicalInfo,
      fetchSaintOfDay,
      ministries,
      addMinistry,
      updateMinistry,
      deleteMinistry,
      getActiveMinistries,
      prayerIntentions,
      addPrayerIntention,
      updatePrayerIntention,
      deletePrayerIntention,
      getPendingIntentions,
      sacraments,
      addSacrament,
      updateSacrament,
      deleteSacrament,
      getActiveSacraments,
      sectionImages,
      addSectionImage,
      updateSectionImage,
      deleteSectionImage,
      getSectionImages
    }}>
      {children}
    </AdminContext.Provider>
  );
};

export const useAdmin = () => {
  const context = useContext(AdminContext);
  if (context === undefined) {
    throw new Error('useAdmin must be used within an AdminProvider');
  }
  return context;
};
