import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useLanguage } from '../../contexts/LanguageContext';
import { Lock, User, Eye, EyeOff, CheckCircle, XCircle, Users, Shield, UserPlus } from 'lucide-react';
import Register from '../Register';
import './Login.css';

const Login: React.FC = () => {
  const { login } = useAuth();
  const { t } = useLanguage();
  const [identifier, setIdentifier] = useState(''); // Changed from username to identifier
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    try {
      const result = await login(identifier, password);

      if (result.success) {
        setSuccess(result.message || 'Login successful!');
        // The App component will handle routing based on user type
      } else {
        setError(result.message || 'Login failed');
      }
    } catch (err) {
      setError('An unexpected error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const fillCredentials = (userType: 'user' | 'admin') => {
    if (userType === 'user') {
      setIdentifier('parishioner');
      setPassword('parish123');
    } else {
      setIdentifier('admin');
      setPassword('admin123');
    }
  };

  // Handle registration success
  const handleRegistrationSuccess = () => {
    setShowRegister(false);
    setSuccess('Registration successful! Please sign in with your credentials.');
  };

  // Show registration form if requested
  if (showRegister) {
    return (
      <Register 
        onSwitchToLogin={() => setShowRegister(false)}
        onRegistrationSuccess={handleRegistrationSuccess}
      />
    );
  }

  return (
    <div className="auth-login">
      <div className="login-container">
        <div className="login-header">
          <div className="church-logo">
            <img 
              src="/api/placeholder/80/80" 
              alt="St. Patrick's Catholic Church Logo" 
              className="logo-image"
            />
          </div>
          <h1>Welcome Back</h1>
          <p>Sign in to St. Patrick's Catholic Church</p>
        </div>

        {/* Quick Login Options */}
        <div className="quick-login-section">
          <div className="quick-login-buttons">
            <button 
              type="button" 
              className="quick-login-btn user-btn"
              onClick={() => fillCredentials('user')}
              title="Fill parishioner credentials"
            >
              <Users size={18} />
              <span>Parishioner Login</span>
            </button>
            <button 
              type="button" 
              className="quick-login-btn admin-btn"
              onClick={() => fillCredentials('admin')}
              title="Fill admin credentials"
            >
              <Shield size={18} />
              <span>Admin Login</span>
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="identifier">Email, Phone, or Username</label>
            <div className="input-wrapper">
              <User className="input-icon" size={20} />
              <input
                type="text"
                id="identifier"
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                placeholder="Enter your email, phone, or username"
                required
                disabled={isLoading}
                autoComplete="username"
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-wrapper">
              <Lock className="input-icon" size={20} />
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
                disabled={isLoading}
                autoComplete="current-password"
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                disabled={isLoading}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
          </div>

          {error && (
            <div className="message error-message">
              <XCircle size={20} />
              <span>{error}</span>
            </div>
          )}

          {success && (
            <div className="message success-message">
              <CheckCircle size={20} />
              <span>{success}</span>
            </div>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={isLoading || !identifier || !password}
          >
            {isLoading ? (
              <>
                <div className="spinner"></div>
                Authenticating...
              </>
            ) : (
              'Sign In'
            )}
          </button>
        </form>

        <div className="auth-options">
          <div className="register-section">
            <p>New to our parish community?</p>
            <button 
              type="button"
              className="register-btn"
              onClick={() => setShowRegister(true)}
            >
              <UserPlus size={20} />
              Register as Parishioner
            </button>
          </div>
        </div>

        <div className="login-footer">
          <p className="help-text">
            Use the quick login buttons above or enter your credentials manually.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
