import React, { createContext, useContext, useState, ReactNode, useEffect } from 'react';

// Types for admin data
export interface Announcement {
  id: string;
  title: string;
  message: string;
  type: 'urgent' | 'info' | 'event';
  isActive: boolean;
  createdAt: string;
  expiresAt?: string;
}

export interface Event {
  id: string;
  title: string;
  description: string;
  date: string;
  time: string;
  location: string;
  category: 'mass' | 'meeting' | 'social' | 'education' | 'outreach';
  isPublished: boolean;
  createdAt: string;
}

export interface GalleryImage {
  id: string;
  title: string;
  description: string;
  url: string;
  category: 'choir' | 'youth' | 'outreach' | 'mass' | 'events';
  uploadedAt: string;
  isPublished: boolean;
}

export interface ContactInfo {
  address: string;
  phone: string[];
  email: string[];
  officeHours: {
    weekdays: string;
    saturday: string;
    sunday: string;
  };
  staff: {
    name: string;
    position: string;
    phone?: string;
    email?: string;
  }[];
}

export interface MassSchedule {
  weekdays: string;
  saturday: string;
  sunday: string[];
  confession: string[];
  specialServices?: {
    name: string;
    date: string;
    time: string;
  }[];
}

interface AdminContextType {
  // Authentication
  isAuthenticated: boolean;
  login: (username: string, password: string) => boolean;
  logout: () => void;

  // Announcements
  announcements: Announcement[];
  addAnnouncement: (announcement: Omit<Announcement, 'id' | 'createdAt'>) => void;
  updateAnnouncement: (id: string, announcement: Partial<Announcement>) => void;
  deleteAnnouncement: (id: string) => void;
  getActiveAnnouncement: () => Announcement | null;

  // Events
  events: Event[];
  addEvent: (event: Omit<Event, 'id' | 'createdAt'>) => void;
  updateEvent: (id: string, event: Partial<Event>) => void;
  deleteEvent: (id: string) => void;
  getPublishedEvents: () => Event[];

  // Gallery
  galleryImages: GalleryImage[];
  addImage: (image: Omit<GalleryImage, 'id' | 'uploadedAt'>) => void;
  updateImage: (id: string, image: Partial<GalleryImage>) => void;
  deleteImage: (id: string) => void;
  getPublishedImages: () => GalleryImage[];

  // Contact & Schedule
  contactInfo: ContactInfo;
  updateContactInfo: (info: Partial<ContactInfo>) => void;
  massSchedule: MassSchedule;
  updateMassSchedule: (schedule: Partial<MassSchedule>) => void;
}

const AdminContext = createContext<AdminContextType | undefined>(undefined);

// Default data
const defaultContactInfo: ContactInfo = {
  address: "St. Patrick's Catholic Church, Makokoba, Bulawayo, Zimbabwe",
  phone: ["+263 9 123456", "+263 77 123 4567"],
  email: ["info@stpatricksmakokoba.org"],
  officeHours: {
    weekdays: "8:00 AM - 4:00 PM",
    saturday: "8:00 AM - 12:00 PM",
    sunday: "After Masses"
  },
  staff: [
    { name: "Fr. Joseph Sibanda", position: "Parish Priest", phone: "+263 77 222 3333" },
    { name: "Fr. Michael Ndlovu", position: "Assistant Priest" },
    { name: "Mrs. Grace Moyo", position: "Parish Secretary", email: "secretary@stpatricksmakokoba.org" }
  ]
};

const defaultMassSchedule: MassSchedule = {
  weekdays: "6:00 AM",
  saturday: "6:00 PM",
  sunday: ["6:00 AM", "8:30 AM (Main)", "5:00 PM"],
  confession: ["Saturday: 5:00 PM - 5:45 PM", "Sunday: Before each Mass", "Or by appointment"]
};

const defaultAnnouncements: Announcement[] = [
  {
    id: "1",
    title: "Christmas Day Service",
    message: "Special Christmas Service - December 25th at 8:30 AM",
    type: "urgent",
    isActive: true,
    createdAt: new Date().toISOString()
  }
];

const defaultEvents: Event[] = [
  {
    id: "1",
    title: "Christmas Day Mass",
    description: "Special Christmas celebration with choir performances in English and IsiNdebele.",
    date: "2024-12-25",
    time: "8:30 AM",
    location: "Main Church",
    category: "mass",
    isPublished: true,
    createdAt: new Date().toISOString()
  },
  {
    id: "2",
    title: "Youth Group Retreat",
    description: "Annual youth retreat focusing on faith formation and community building.",
    date: "2025-01-15",
    time: "9:00 AM - 5:00 PM",
    location: "Parish Hall",
    category: "social",
    isPublished: true,
    createdAt: new Date().toISOString()
  }
];

export const AdminProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  // Authentication state
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Data states
  const [announcements, setAnnouncements] = useState<Announcement[]>(defaultAnnouncements);
  const [events, setEvents] = useState<Event[]>(defaultEvents);
  const [galleryImages, setGalleryImages] = useState<GalleryImage[]>([]);
  const [contactInfo, setContactInfo] = useState<ContactInfo>(defaultContactInfo);
  const [massSchedule, setMassSchedule] = useState<MassSchedule>(defaultMassSchedule);

  // Load data from localStorage on mount
  useEffect(() => {
    const savedAuth = localStorage.getItem('stpatricks_admin_auth');
    const savedAnnouncements = localStorage.getItem('stpatricks_announcements');
    const savedEvents = localStorage.getItem('stpatricks_events');
    const savedImages = localStorage.getItem('stpatricks_gallery');
    const savedContact = localStorage.getItem('stpatricks_contact');
    const savedSchedule = localStorage.getItem('stpatricks_schedule');

    if (savedAuth === 'true') setIsAuthenticated(true);
    if (savedAnnouncements) setAnnouncements(JSON.parse(savedAnnouncements));
    if (savedEvents) setEvents(JSON.parse(savedEvents));
    if (savedImages) setGalleryImages(JSON.parse(savedImages));
    if (savedContact) setContactInfo(JSON.parse(savedContact));
    if (savedSchedule) setMassSchedule(JSON.parse(savedSchedule));
  }, []);

  // Authentication functions
  const login = (username: string, password: string): boolean => {
    // Simple authentication - in production, use proper authentication
    if (username === 'admin' && password === 'stpatricks2024') {
      setIsAuthenticated(true);
      localStorage.setItem('stpatricks_admin_auth', 'true');
      return true;
    }
    return false;
  };

  const logout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('stpatricks_admin_auth');
  };

  // Announcement functions
  const addAnnouncement = (announcement: Omit<Announcement, 'id' | 'createdAt'>) => {
    const newAnnouncement: Announcement = {
      ...announcement,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...announcements, newAnnouncement];
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const updateAnnouncement = (id: string, announcement: Partial<Announcement>) => {
    const updated = announcements.map(a => a.id === id ? { ...a, ...announcement } : a);
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const deleteAnnouncement = (id: string) => {
    const updated = announcements.filter(a => a.id !== id);
    setAnnouncements(updated);
    localStorage.setItem('stpatricks_announcements', JSON.stringify(updated));
  };

  const getActiveAnnouncement = (): Announcement | null => {
    const active = announcements.find(a => a.isActive);
    if (!active) return null;

    // Check if announcement has expired
    if (active.expiresAt && new Date(active.expiresAt) < new Date()) {
      updateAnnouncement(active.id, { isActive: false });
      return null;
    }

    return active;
  };

  // Event functions
  const addEvent = (event: Omit<Event, 'id' | 'createdAt'>) => {
    const newEvent: Event = {
      ...event,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...events, newEvent];
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const updateEvent = (id: string, event: Partial<Event>) => {
    const updated = events.map(e => e.id === id ? { ...e, ...event } : e);
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const deleteEvent = (id: string) => {
    const updated = events.filter(e => e.id !== id);
    setEvents(updated);
    localStorage.setItem('stpatricks_events', JSON.stringify(updated));
  };

  const getPublishedEvents = (): Event[] => {
    return events.filter(e => e.isPublished).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  };

  // Gallery functions
  const addImage = (image: Omit<GalleryImage, 'id' | 'uploadedAt'>) => {
    const newImage: GalleryImage = {
      ...image,
      id: Date.now().toString(),
      uploadedAt: new Date().toISOString()
    };
    const updated = [...galleryImages, newImage];
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const updateImage = (id: string, image: Partial<GalleryImage>) => {
    const updated = galleryImages.map(i => i.id === id ? { ...i, ...image } : i);
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const deleteImage = (id: string) => {
    const updated = galleryImages.filter(i => i.id !== id);
    setGalleryImages(updated);
    localStorage.setItem('stpatricks_gallery', JSON.stringify(updated));
  };

  const getPublishedImages = (): GalleryImage[] => {
    return galleryImages.filter(i => i.isPublished);
  };

  // Contact and schedule functions
  const updateContactInfo = (info: Partial<ContactInfo>) => {
    const updated = { ...contactInfo, ...info };
    setContactInfo(updated);
    localStorage.setItem('stpatricks_contact', JSON.stringify(updated));
  };

  const updateMassSchedule = (schedule: Partial<MassSchedule>) => {
    const updated = { ...massSchedule, ...schedule };
    setMassSchedule(updated);
    localStorage.setItem('stpatricks_schedule', JSON.stringify(updated));
  };

  return (
    <AdminContext.Provider value={{
      isAuthenticated,
      login,
      logout,
      announcements,
      addAnnouncement,
      updateAnnouncement,
      deleteAnnouncement,
      getActiveAnnouncement,
      events,
      addEvent,
      updateEvent,
      deleteEvent,
      getPublishedEvents,
      galleryImages,
      addImage,
      updateImage,
      deleteImage,
      getPublishedImages,
      contactInfo,
      updateContactInfo,
      massSchedule,
      updateMassSchedule
    }}>
      {children}
    </AdminContext.Provider>
  );
};

export const useAdmin = () => {
  const context = useContext(AdminContext);
  if (context === undefined) {
    throw new Error('useAdmin must be used within an AdminProvider');
  }
  return context;
};
