    const updated = prayerIntentions.filter(p => p.id !== id);
    setPrayerIntentions(updated);
    localStorage.setItem('stpatricks_prayer_intentions', JSON.stringify(updated));
  };

  const getPendingIntentions = (): PrayerIntention[] => {
    return prayerIntentions.filter(p => p.status === 'pending');
  };

  // Sacrament functions
  const addSacrament = (sacrament: Omit<Sacrament, 'id' | 'createdAt'>) => {
    const newSacrament: Sacrament = {
      ...sacrament,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...sacraments, newSacrament];
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const updateSacrament = (id: string, sacrament: Partial<Sacrament>) => {
    const updated = sacraments.map(s => s.id === id ? { ...s, ...sacrament } : s);
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const deleteSacrament = (id: string) => {
    const updated = sacraments.filter(s => s.id !== id);
    setSacraments(updated);
    localStorage.setItem('stpatricks_sacraments', JSON.stringify(updated));
  };

  const getActiveSacraments = (): Sacrament[] => {
    return sacraments.filter(s => s.isActive);
  };

  // Section Image functions
  const addSectionImage = (image: Omit<SectionImage, 'id' | 'createdAt'>) => {
    const newImage: SectionImage = {
      ...image,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...sectionImages, newImage];
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const updateSectionImage = (id: string, image: Partial<SectionImage>) => {
    const updated = sectionImages.map(i => i.id === id ? { ...i, ...image } : i);
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const deleteSectionImage = (id: string) => {
    const updated = sectionImages.filter(i => i.id !== id);
    setSectionImages(updated);
    localStorage.setItem('stpatricks_section_images', JSON.stringify(updated));
  };

  const getSectionImages = (section: SectionImage['section']): SectionImage[] => {
    return sectionImages.filter(i => i.section === section && i.isActive);
  };

  // Parish News functions
  const addParishNews = (news: Omit<ParishNews, 'id' | 'createdAt'>) => {
    const newNews: ParishNews = {
      ...news,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    const updated = [...parishNews, newNews];
    setParishNews(updated);
    localStorage.setItem('stpatricks_parish_news', JSON.stringify(updated));
  };

  const updateParishNews = (id: string, news: Partial<ParishNews>) => {
    const updated = parishNews.map(n => n.id === id ? { ...n, ...news } : n);
    setParishNews(updated);
    localStorage.setItem('stpatricks_parish_news', JSON.stringify(updated));
  };

  const deleteParishNews = (id: string) => {
    const updated = parishNews.filter(n => n.id !== id);
    setParishNews(updated);
    localStorage.setItem('stpatricks_parish_news', JSON.stringify(updated));
  };

  const getPublishedParishNews = (): ParishNews[] => {
    return parishNews.filter(n => n.isPublished && !n.isArchived);
  };

  const archiveParishNews = (id: string) => {
    const newsItem = parishNews.find(n => n.id === id);
    if (newsItem) {
      // Add to archive
      const archiveItem: NewsArchive = {
        id: Date.now().toString(),
        title: newsItem.title,
        summary: newsItem.summary,
        content: newsItem.content,
        imageUrl: newsItem.imageUrl,
        author: newsItem.author,
        originalPublishDate: newsItem.publishedAt,
        archivedAt: new Date().toISOString(),
        year: new Date(newsItem.publishedAt).getFullYear(),
        month: new Date(newsItem.publishedAt).getMonth() + 1
      };
      const updatedArchive = [...newsArchive, archiveItem];
      setNewsArchive(updatedArchive);
      localStorage.setItem('stpatricks_news_archive', JSON.stringify(updatedArchive));

      // Mark as archived
      updateParishNews(id, { isArchived: true });
    }
  };

  // External News functions
  const fetchExternalNews = async (source: ExternalNews['source']) => {
    try {
      // Mock API calls - in real implementation, these would call actual APIs
      let mockNews: ExternalNews[] = [];

      switch (source) {
        case 'diocese':
          mockNews = [
            {
              id: Date.now().toString(),
              title: "Archbishop's Pastoral Letter on Unity",
              summary: "New pastoral letter from the Archdiocese of Bulawayo on Christian unity",
              imageUrl: "/api/placeholder/300/200",
              externalUrl: "https://archdiocesebulawayo.org/pastoral-letter-unity",
              source: 'diocese',
              publishedAt: new Date().toISOString(),
              fetchedAt: new Date().toISOString()
            }
          ];
          break;
        case 'vatican':
          mockNews = [
            {
              id: Date.now().toString(),
              title: "Pope Francis' Message for World Peace Day",
              summary: "Holy Father's message emphasizing peace and reconciliation",
              imageUrl: "/api/placeholder/300/200",
              externalUrl: "https://vatican.va/content/francesco/en/messages/peace/documents/20241201-messaggio-57giornatamondiale-pace2025.html",
              source: 'vatican',
              publishedAt: new Date().toISOString(),
              fetchedAt: new Date().toISOString()
            }
          ];
          break;
        case 'zimbabwe_catholic':
          mockNews = [
            {
              id: Date.now().toString(),
              title: "ZCBC Statement on National Development",
              summary: "Zimbabwe Catholic Bishops Conference statement on national development priorities",
              imageUrl: "/api/placeholder/300/200",
              externalUrl: "https://zcbc.co.zw/statement-national-development",
              source: 'zimbabwe_catholic',
              publishedAt: new Date().toISOString(),
              fetchedAt: new Date().toISOString()
            }
          ];
          break;
      }

      const updated = [...externalNews.filter(n => n.source !== source), ...mockNews];
      setExternalNews(updated);
      localStorage.setItem('stpatricks_external_news', JSON.stringify(updated));
    } catch (error) {
      console.error(`Failed to fetch ${source} news:`, error);
    }
  };

  // News Archive functions
  const getNewsArchiveByYear = (year: number): NewsArchive[] => {
    return newsArchive.filter(n => n.year === year);
  };

  const getNewsArchiveByMonth = (year: number, month: number): NewsArchive[] => {
    return newsArchive.filter(n => n.year === year && n.month === month);
  };

  return (
    <AdminContext.Provider value={{
      isAuthenticated,
      login,
      logout,
      announcements,
      addAnnouncement,
      updateAnnouncement,
      deleteAnnouncement,
      getActiveAnnouncement,
      events,
      addEvent,
      updateEvent,
      deleteEvent,
      getPublishedEvents,
      galleryImages,
      addImage,
      updateImage,
      deleteImage,
      getPublishedImages,
      contactInfo,
      updateContactInfo,
      massSchedule,
      updateMassSchedule,
      themesOfYear,
      addThemeOfYear,
      updateThemeOfYear,
      deleteThemeOfYear,
      getActiveTheme,
      saintOfDay,
      liturgicalInfo,
      fetchSaintOfDay,
      ministries,
      addMinistry,
      updateMinistry,
      deleteMinistry,
      getActiveMinistries,
      prayerIntentions,
      addPrayerIntention,
      updatePrayerIntention,
      deletePrayerIntention,
      getPendingIntentions,
      sacraments,
      addSacrament,
      updateSacrament,
      deleteSacrament,
      getActiveSacraments,
      sectionImages,
      addSectionImage,
      updateSectionImage,
      deleteSectionImage,
      getSectionImages
    }}>
      {children}
    </AdminContext.Provider>
  );
};

export const useAdmin = () => {
  const context = useContext(AdminContext);
  if (context === undefined) {
    throw new Error('useAdmin must be used within an AdminProvider');
  }
  return context;
};
