import React from 'react';
import { useLanguage } from '../contexts/LanguageContext';

const About: React.FC = () => {
  const { t } = useLanguage();

  return (
    <div className="about section-padding">
      <div className="container">
        <h1>{t('nav.about')}</h1>
        <div className="grid grid-2">
          <div className="card">
            <h3>Our History</h3>
            <p>St. Patrick's Catholic Church has been serving the Makokoba community in Bulawayo for over 50 years, providing spiritual guidance and community support.</p>
          </div>
          <div className="card">
            <h3>Our Mission</h3>
            <p>To serve God and our community through worship, outreach, and social justice, embracing both English and IsiNdebele speaking parishioners.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default About;
