import React, { useState } from 'react';
import { useAdmin } from '../../contexts/AdminContext';
import { useAuth } from '../../contexts/AuthContext';
import AnnouncementManager from '../../components/admin/AnnouncementManager';
import GalleryManager from '../../components/admin/GalleryManager';
import EventManager from '../../components/admin/EventManager';
import PrayerManager from '../../components/admin/PrayerManager';
import ImageManager from '../../components/admin/ImageManager';
import { 
  LogOut, 
  Bell, 
  Calendar, 
  Image, 
  Phone, 
  Clock, 
  Users,
  BarChart3,
  Settings,
  Plus,
  Edit,
  Trash2,
  Eye,
  EyeOff,
  BookOpen,
  TrendingUp,
  Activity,
  Heart,
  UserCog,
  Shield,
  AlertTriangle,
  Info
} from 'lucide-react';
import './AdminDashboard.css';

type AdminSection = 'overview' | 'announcements' | 'events' | 'gallery' | 'contact' | 'schedule' | 'priests-desk' | 'analytics' | 'prayers' | 'images' | 'users';

const AdminDashboard: React.FC = () => {
  const { 
    announcements, 
    events, 
    galleryImages, 
    getActiveAnnouncement,
    getPublishedEvents,
    getPublishedImages
  } = useAdmin();

  const { logout } = useAuth();

  const [activeSection, setActiveSection] = useState<AdminSection>('overview');

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      logout();
    }
  };

  // Mock data for parish members and website analytics
  const parishMembers = [
    { id: '1', name: 'John Doe', email: 'john@example.com', lastLogin: new Date(), status: 'online' },
    { id: '2', name: 'Mary Smith', email: 'mary@example.com', lastLogin: new Date(Date.now() - 300000), status: 'online' },
    { id: '3', name: 'Peter Johnson', email: 'peter@example.com', lastLogin: new Date(Date.now() - 600000), status: 'online' },
    { id: '4', name: 'Sarah Wilson', email: 'sarah@example.com', lastLogin: new Date(Date.now() - 1800000), status: 'away' },
    { id: '5', name: 'Michael Brown', email: 'michael@example.com', lastLogin: new Date(Date.now() - 86400000), status: 'offline' }
  ];

  const websiteAnalytics = {
    totalVisitors: 15420,
    todayVisitors: 234,
    pageViews: 45680,
    avgSessionDuration: '4:32',
    bounceRate: 32.5,
    topPages: [
      { page: 'Home', views: 12450, percentage: 27.3 },
      { page: 'Mass Times', views: 8920, percentage: 19.5 },
      { page: 'Events', views: 6780, percentage: 14.8 },
      { page: 'Gallery', views: 5430, percentage: 11.9 },
      { page: 'Contact', views: 4200, percentage: 9.2 }
    ],
    monthlyData: [
      { month: 'Jan', visitors: 1200, pageViews: 3600 },
      { month: 'Feb', visitors: 1450, pageViews: 4200 },
      { month: 'Mar', visitors: 1680, pageViews: 4890 },
      { month: 'Apr', visitors: 1920, pageViews: 5760 },
      { month: 'May', visitors: 2100, pageViews: 6300 },
      { month: 'Jun', visitors: 1850, pageViews: 5550 }
    ]
  };

  const stats = {
    totalAnnouncements: announcements.length,
    activeAnnouncements: announcements.filter(a => a.isActive).length,
    totalEvents: events.length,
    publishedEvents: getPublishedEvents().length,
    totalImages: galleryImages.length,
    publishedImages: getPublishedImages().length,
    parishMembers: {
      total: parishMembers.length,
      online: parishMembers.filter(m => m.status === 'online').length,
      away: parishMembers.filter(m => m.status === 'away').length,
      offline: parishMembers.filter(m => m.status === 'offline').length,
      activeToday: parishMembers.filter(m => 
        new Date().getTime() - m.lastLogin.getTime() < 86400000
      ).length
    },
    website: websiteAnalytics
  };

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <aside className="admin-sidebar">
        <div className="sidebar-header">
          <div className="admin-logo">
            <img 
              src="/api/placeholder/60/60" 
