    setUsers([...users, user]);
    setNewUser({ username: '', email: '', role: 'user', password: '' });
    setShowAddUser(false);
  };

  const handleResetPassword = (userId: string) => {
    const user = users.find(u => u.id === userId);
    if (user) {
      setResetUserId(userId);
      setResetUserName(user.username);
      setShowResetPassword(true);
      setResetPassword('');
      setConfirmPassword('');
      setResetPasswordError('');
    }
  };

  const handleConfirmPasswordReset = () => {
    // Validate passwords
    if (!resetPassword || !confirmPassword) {
      setResetPasswordError('Please enter both password fields');
      return;
    }

    if (resetPassword.length < 6) {
      setResetPasswordError('Password must be at least 6 characters long');
      return;
    }

    if (resetPassword !== confirmPassword) {
      setResetPasswordError('Passwords do not match');
      return;
    }

    // Update user password (in real app, this would call an API)
    setUsers(users.map(user => 
      user.id === resetUserId 
        ? { ...user, password: resetPassword }
        : user
    ));

    // Close modal and reset state
    setShowResetPassword(false);
    setResetUserId('');
    setResetUserName('');
    setResetPassword('');
    setConfirmPassword('');
    setResetPasswordError('');

    alert(`Password successfully reset for user: ${resetUserName}`);
  };

  const handleCancelPasswordReset = () => {
    setShowResetPassword(false);
    setResetUserId('');
    setResetUserName('');
    setResetPassword('');
    setConfirmPassword('');
    setResetPasswordError('');
  };

  // Function to prevent copy/paste
  const handlePasswordInputEvents = (e: React.ClipboardEvent<HTMLInputElement> | React.KeyboardEvent<HTMLInputElement>) => {
    if (e.type === 'paste' || e.type === 'copy' || e.type === 'cut') {
      e.preventDefault();
      return false;
    }

    // Prevent Ctrl+V, Ctrl+C, Ctrl+X
    if (e.type === 'keydown') {
      const keyEvent = e as React.KeyboardEvent<HTMLInputElement>;
      if (keyEvent.ctrlKey && (keyEvent.key === 'v' || keyEvent.key === 'c' || keyEvent.key === 'x')) {
        e.preventDefault();
        return false;
      }
    }
  };

  const handleToggleStatus = (userId: string) => {
    setUsers(users.map(user => 
      user.id === userId 
        ? { ...user, status: user.status === 'active' ? 'inactive' : 'active' }
        : user
    ));
  };

  const handleDeleteUser = (userId: string) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      setUsers(users.filter(user => user.id !== userId));
    }
  };

  return (
    <div className="user-management-section">
      <div className="section-header">
        <h3>User Management</h3>
        <button 
          className="btn btn-primary"
          onClick={() => setShowAddUser(true)}
        >
          <Plus size={16} />
          Add New User
        </button>
      </div>

      {showAddUser && (
        <div className="add-user-form">
          <h4>Add New User</h4>
          <div className="form-row">
            <input
              type="text"
              placeholder="Username"
              value={newUser.username}
              onChange={(e) => setNewUser({...newUser, username: e.target.value})}
            />
            <input
              type="email"
              placeholder="Email"
              value={newUser.email}
              onChange={(e) => setNewUser({...newUser, email: e.target.value})}
            />
            <select
              value={newUser.role}
              onChange={(e) => setNewUser({...newUser, role: e.target.value})}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
            <input
              type="password"
              placeholder="Password"
              value={newUser.password}
              onChange={(e) => setNewUser({...newUser, password: e.target.value})}
            />
          </div>
          <div className="form-actions">
            <button className="btn btn-success" onClick={handleAddUser}>Add User</button>
            <button className="btn btn-secondary" onClick={() => setShowAddUser(false)}>Cancel</button>
          </div>
        </div>
      )}

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th>Last Login</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>
                    {user.role === 'admin' ? <Shield size={12} /> : <Users size={12} />}
                    {user.role}
                  </span>
                </td>
                <td>
                  <span className={`status-badge ${user.status}`}>
                    {user.status}
                  </span>
                </td>
                <td>{user.lastLogin}</td>
                <td className="actions">
                  <button 
                    className="btn-icon"
                    onClick={() => handleResetPassword(user.id)}
                    title="Reset Password"
                  >
                    <Settings size={16} />
                  </button>
                  <button 
                    className="btn-icon"
                    onClick={() => handleToggleStatus(user.id)}
                    title={user.status === 'active' ? 'Deactivate' : 'Activate'}
                  >
                    {user.status === 'active' ? <EyeOff size={16} /> : <Eye size={16} />}
                  </button>
                  <button 
                    className="btn-icon danger"
                    onClick={() => handleDeleteUser(user.id)}
                    title="Delete User"
                  >
                    <Trash2 size={16} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Password Reset Modal */}
      {showResetPassword && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Reset Password for {resetUserName}</h3>
            </div>
            <div className="modal-body">
              {resetPasswordError && (
                <div className="error-message">
                  <AlertTriangle size={16} />
                  {resetPasswordError}
                </div>
              )}
              <div className="form-group">
                <label>New Password:</label>
                <input
                  type="password"
                  value={resetPassword}
                  onChange={(e) => setResetPassword(e.target.value)}
                  onPaste={handlePasswordInputEvents}
                  onCopy={handlePasswordInputEvents}
                  onCut={handlePasswordInputEvents}
                  onKeyDown={handlePasswordInputEvents}
                  placeholder="Enter new password (min 6 characters)"
                  autoComplete="new-password"
                />
              </div>
              <div className="form-group">
                <label>Confirm Password:</label>
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  onPaste={handlePasswordInputEvents}
                  onCopy={handlePasswordInputEvents}
                  onCut={handlePasswordInputEvents}
                  onKeyDown={handlePasswordInputEvents}
                  placeholder="Confirm new password"
                  autoComplete="new-password"
                />
              </div>
              <div className="password-requirements">
                <small>
                   Password must be at least 6 characters long<br/>
                   Copy/paste is disabled for security
                </small>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn btn-success" 
                onClick={handleConfirmPasswordReset}
                disabled={!resetPassword || !confirmPassword}
              >
                Reset Password
              </button>
              <button 
                className="btn btn-secondary" 
                onClick={handleCancelPasswordReset}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};


export default AdminDashboard;
