import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { Newspaper, Globe, Church, Calendar, ExternalLink } from 'lucide-react';
import './News.css';

const News: React.FC = () => {
  const { t } = useLanguage();
  const [activeTab, setActiveTab] = useState<'parish' | 'diocese' | 'vatican' | 'zimbabwe'>('parish');

  const parishNews = [
    {
      id: 1,
      title: "Christmas Mass Schedule Announced",
      date: "2024-12-15",
      excerpt: "Special Christmas services will be held with extended hours to accommodate all parishioners.",
      content: "We are pleased to announce our Christmas Mass schedule. Christmas Eve Mass will be at 11:00 PM, and Christmas Day will have Masses at 6:00 AM, 8:30 AM, and 5:00 PM. The choir will perform special Christmas carols in both English and IsiNdebele.",
      category: "Liturgy"
    },
    {
      id: 2,
      title: "Youth Group Fundraising Success",
      date: "2024-12-10",
      excerpt: "The Catholic Youth Association raised funds for local orphanage through their charity drive.",
      content: "Our Catholic Youth Association has successfully raised $2,500 for the local orphanage through their recent charity drive. The funds will be used to purchase school supplies and uniforms for children in need. We thank all parishioners who contributed to this worthy cause.",
      category: "Community"
    },
    {
      id: 3,
      title: "New Catechism Classes Starting",
      date: "2024-12-05",
      excerpt: "Adult catechism classes will begin in January for those seeking to deepen their faith.",
      content: "Starting January 2025, we will offer adult catechism classes every Wednesday at 7:00 PM. These classes are open to all adults who wish to deepen their understanding of Catholic teachings. Registration is now open at the parish office.",
      category: "Education"
    }
  ];

  const dioceseNews = [
    {
      id: 1,
      title: "Archbishop's Christmas Message",
      date: "2024-12-20",
      excerpt: "His Grace shares a message of hope and unity for the Christmas season.",
      content: "Archbishop Ndlovu has released his annual Christmas message, emphasizing the importance of unity and compassion during these challenging times. He calls upon all Catholics in the diocese to reach out to those in need.",
      category: "Episcopal",
      source: "Bulawayo Archdiocese"
    },
    {
      id: 2,
      title: "Diocesan Synod Preparations",
      date: "2024-12-12",
      excerpt: "Preparations underway for the upcoming diocesan synod in 2025.",
      content: "The Archdiocese of Bulawayo is preparing for a diocesan synod to be held in March 2025. Parish representatives are being selected to participate in discussions about the future direction of the local church.",
      category: "Synod",
      source: "Bulawayo Archdiocese"
    },
    {
      id: 3,
      title: "New Seminary Students Ordained",
      date: "2024-12-01",
      excerpt: "Five new deacons were ordained at the cathedral last weekend.",
      content: "Five seminarians from St. Augustine's Seminary were ordained as deacons in a beautiful ceremony at the Cathedral of Mary and All Saints. They will serve in various parishes throughout the archdiocese.",
      category: "Ordination",
      source: "Bulawayo Archdiocese"
    }
  ];

  const vaticanNews = [
    {
      id: 1,
      title: "Pope Francis' Christmas Urbi et Orbi",
      date: "2024-12-25",
      excerpt: "Holy Father delivers Christmas message to the world from St. Peter's Basilica.",
      content: "Pope Francis delivered his annual Christmas Urbi et Orbi message, calling for peace in conflict zones and compassion for refugees worldwide. The Pope emphasized the importance of welcoming the stranger and caring for the poor.",
      category: "Papal",
      source: "Vatican News"
    },
    {
      id: 2,
      title: "Synod on Synodality Continues",
      date: "2024-12-18",
      excerpt: "The global synodal process continues with local church consultations.",
      content: "The Vatican has announced the continuation of the Synod on Synodality process, with local churches worldwide invited to participate in ongoing consultations about the future of the Catholic Church.",
      category: "Synod",
      source: "Vatican News"
    },
    {
      id: 3,
      title: "New Saints Canonized",
      date: "2024-12-08",
      excerpt: "Pope Francis canonizes three new saints during Feast of the Immaculate Conception.",
      content: "During the celebration of the Feast of the Immaculate Conception, Pope Francis canonized three new saints, including a missionary who served in Africa and two martyrs from Asia.",
      category: "Canonization",
      source: "Vatican News"
    }
  ];

  const zimbabweNews = [
    {
      id: 1,
      title: "ZCBC Calls for Peace and Unity",
      date: "2024-12-20",
      excerpt: "Zimbabwe Catholic Bishops Conference issues pastoral letter on national unity.",
      content: "The Zimbabwe Catholic Bishops Conference has issued a pastoral letter calling for peace, unity, and reconciliation among all Zimbabweans. The bishops emphasized the importance of dialogue and mutual respect in building a better nation.",
      category: "Pastoral",
      source: "ZCBC"
    },
    {
      id: 2,
      title: "Catholic Education Week Celebrated",
      date: "2024-12-10",
      excerpt: "Catholic schools across Zimbabwe celebrate Catholic Education Week with various activities.",
      content: "Catholic schools throughout Zimbabwe celebrated Catholic Education Week with special masses, cultural activities, and community service projects. The theme focused on 'Education for Integral Human Development'.",
      category: "Education",
      source: "Catholic Church News Zimbabwe"
    },
    {
      id: 3,
      title: "Caritas Zimbabwe Launches Food Security Program",
      date: "2024-12-05",
      excerpt: "New initiative aims to address food insecurity in rural communities.",
      content: "Caritas Zimbabwe has launched a comprehensive food security program targeting vulnerable rural communities. The program includes agricultural training, seed distribution, and nutrition education.",
      category: "Social Justice",
      source: "ZCBC"
    }
  ];

  const getCurrentNews = () => {
    switch (activeTab) {
      case 'parish': return parishNews;
      case 'diocese': return dioceseNews;
      case 'vatican': return vaticanNews;
      default: return parishNews;
    }
  };

  const getTabIcon = (tab: string) => {
    switch (tab) {
      case 'parish': return <Church size={20} />;
      case 'diocese': return <Newspaper size={20} />;
      case 'vatican': return <Globe size={20} />;
      default: return <Newspaper size={20} />;
    }
  };

  const getCategoryColor = (category: string) => {
    const colors = {
      'Liturgy': '#2d5016',
      'Community': '#45b7d1',
      'Education': '#f39c12',
      'Episcopal': '#8e44ad',
      'Synod': '#e74c3c',
      'Ordination': '#27ae60',
      'Papal': '#f1c40f',
      'Canonization': '#e67e22'
    };
    return colors[category as keyof typeof colors] || '#6c757d';
  };

  return (
    <div className="news section-padding">
      <div className="container">
        <div className="news-header">
          <h1 className="text-center mb-4">Parish News & Updates</h1>
          <p className="text-center mb-4">Stay informed with the latest news from our parish, diocese, and the universal Church</p>
        </div>

        {/* News Tabs */}
        <div className="news-tabs">
          <button
            className={`news-tab ${activeTab === 'parish' ? 'active' : ''}`}
            onClick={() => setActiveTab('parish')}
          >
            {getTabIcon('parish')}
            <span>Parish News</span>
          </button>
          <button
            className={`news-tab ${activeTab === 'diocese' ? 'active' : ''}`}
            onClick={() => setActiveTab('diocese')}
          >
            {getTabIcon('diocese')}
            <span>Diocese News</span>
          </button>
          <button
            className={`news-tab ${activeTab === 'vatican' ? 'active' : ''}`}
            onClick={() => setActiveTab('vatican')}
          >
            {getTabIcon('vatican')}
            <span>Vatican News</span>
          </button>
        </div>

        {/* News Content */}
        <div className="news-content">
          <div className="news-grid">
            {getCurrentNews().map((article) => (
              <article key={article.id} className="news-card card">
                <div className="news-card-header">
                  <div className="news-meta">
                    <span 
                      className="category-badge"
                      style={{ backgroundColor: getCategoryColor(article.category) }}
                    >
                      {article.category}
                    </span>
                    <div className="news-date">
                      <Calendar size={16} />
                      <span>{new Date(article.date).toLocaleDateString()}</span>
                    </div>
                  </div>
                  {('source' in article) && (
                    <div className="news-source">
                      <ExternalLink size={16} />
                      <span>{(article as any).source}</span>
                    </div>
                  )}
                </div>

                <div className="news-card-content">
                  <h3>{article.title}</h3>
                  <p className="news-excerpt">{article.excerpt}</p>
                  <p className="news-content-text">{article.content}</p>
                </div>

                <div className="news-card-footer">
                  <button className="read-more-btn">
                    Read Full Article
                    <ExternalLink size={16} />
                  </button>
                </div>
              </article>
            ))}
          </div>
        </div>

        {/* News Archive */}
        <div className="news-archive card">
          <h3>News Archive</h3>
          <p>Looking for older news articles? Visit our news archive to browse previous announcements and updates.</p>
          <button className="btn btn-secondary">
            <Newspaper size={20} />
            View Archive
          </button>
        </div>
      </div>
    </div>
  );
};

export default News;
