import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useLanguage } from '../contexts/LanguageContext';
import { useAdmin } from '../contexts/AdminContext';
import { useAuth } from '../contexts/AuthContext';
import { Menu, X, Globe, LogOut, User } from 'lucide-react';
import './Header.css';

const Header: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { language, setLanguage, t } = useLanguage();
  const { getActiveAnnouncement } = useAdmin();
  const { user, logout } = useAuth();
  const location = useLocation();

  const activeAnnouncement = getActiveAnnouncement();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleLanguage = () => {
    if (language === 'en') {
      setLanguage('nd');
    } else if (language === 'nd') {
      setLanguage('sn');
    } else {
      setLanguage('en');
    }
  };

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className="header">
      {/* Dynamic Announcements Banner */}
      {activeAnnouncement && (
        <div className={`urgent-banner ${activeAnnouncement.type}`}>
          <div className="container">
            <p className="urgent-text">
              ðŸ“¢ {activeAnnouncement.title}: {activeAnnouncement.message}
            </p>
          </div>
        </div>
      )}

      <div className="header-main">
        <div className="container">
          <div className="header-content">
            {/* Logo and Church Name */}
            <div className="logo-section">
              <div className="logo" onClick={() => window.location.reload()}>
                <img 
                  src="/api/placeholder/80/80" 
                  alt="St. Patrick's Catholic Church Logo" 
                  className="logo-image"
                />
              </div>
              <div className="church-info">
                <h1 className="church-name">St. Patrick's</h1>
                <p className="church-location">Makokoba, Bulawayo</p>
              </div>
            </div>

            {/* User Actions */}
            <div className="user-actions">
              {/* Language Toggle */}
              <div className="language-toggle">
                <button 
                  onClick={toggleLanguage}
                  className="lang-btn"
                  aria-label="Toggle Language"
                >
                  <Globe size={20} />
                  <span>
                    {language === 'en' && t('lang.switch_to_ndebele')}
                    {language === 'nd' && t('lang.switch_to_shona')}
                    {language === 'sn' && t('lang.switch_to_english')}
                  </span>
                </button>
              </div>

              {/* User Info & Logout */}
              {user && (
                <div className="user-info">
                  <div className="user-details">
                    <User size={16} />
                    <span className="username">{user.username}</span>
                    <span className="user-type">({user.userType})</span>
                  </div>
                  <button 
                    onClick={logout}
                    className="logout-btn"
                    aria-label="Logout"
                    title="Logout"
                  >
                    <LogOut size={18} />
                  </button>
                </div>
              )}
            </div>

            {/* Mobile Menu Button */}
            <button 
              className="mobile-menu-btn"
              onClick={toggleMenu}
              aria-label="Toggle Menu"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>

          {/* Navigation */}
          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li>
                <Link 
                  to="/" 
                  className={`nav-link ${isActive('/') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.home')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/prayers" 
                  className={`nav-link ${isActive('/prayers') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  Prayers & Daily Devotion
                </Link>
              </li>
              <li>
                <Link 
                  to="/gallery" 
                  className={`nav-link ${isActive('/gallery') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  Gallery
                </Link>
              </li>
              <li>
                <Link 
                  to="/ministries" 
                  className={`nav-link ${isActive('/ministries') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.ministries')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/outreach" 
                  className={`nav-link ${isActive('/outreach') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.outreach')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/sacraments" 
                  className={`nav-link ${isActive('/sacraments') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.sacraments')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/calendar" 
                  className={`nav-link ${isActive('/calendar') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  Calendar
                </Link>
              </li>
              <li>
                <Link 
                  to="/news" 
                  className={`nav-link ${isActive('/news') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  News
                </Link>
              </li>
              <li>
                <Link 
                  to="/contact" 
                  className={`nav-link ${isActive('/contact') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.contact')}
                </Link>
              </li>
              <li>
                <Link 
                  to="/giving" 
                  className={`nav-link nav-link-giving ${isActive('/giving') ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {t('nav.giving')}
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
